function(doc) {
  var d=new Date(doc.date)
  if ((doc.id)&& (doc.id != doc.sensorid)){
  	emit([doc.id,'temp',d.getUTCFullYear(),d.getUTCMonth()+1,d.getUTCDate(),d.getUTCHours()], doc.temp);
  	emit([doc.id,'humid',d.getUTCFullYear(),d.getUTCMonth()+1,d.getUTCDate(),d.getUTCHours()], doc.humid);
	}
}

